interface Icon {
  getChannelIcon(): string,
  setChannelIcon(image: string): void
}

export class ChannelIcon implements Icon {
  private icon: string = "data:image/svg+xml;base64,"
  constructor() {}

  getChannelIcon(): string {
    return this.icon
  }

  setChannelIcon(image: string): void {
    this.icon = image
  }
}
